import styled from "styled-components"

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  padding: 20px;

  input {
    margin-bottom: 30px;
    height: 40px;
    font-size: 16px;
  }

  select {
    margin-bottom: 30px;
    height: 40px;
    font-size: 16px;
  }

  button {
    font-size: 16px;
    height: 40px;
    cursor: pointer;
  }

  label {
    font-size: 18px;
    font-weight: bold;
    margin-bottom: 10px;
  }
`
