import { Item } from "core/types"
import { ChangeEvent } from "react"
import { BsGiftFill } from "react-icons/bs"

import * as S from "./styles"

interface Props {
  item: Item
  onOwnerChange?: (value: string) => void
}

export function CheckItem({ item, onOwnerChange }: Props) {
  function handleChange(event: ChangeEvent<HTMLInputElement>) {
    if (onOwnerChange) {
      onOwnerChange(event.target.value)
    }
  }

  return (
    <S.Container>
      <S.Name>{item.name}</S.Name>
      <S.OwnerContainer>
        {item.owner && (
          <S.Owner>
            <BsGiftFill size={24} /> {item.owner}
          </S.Owner>
        )}
        {!item.owner && (
          <S.UpdateOwnerButton>{"QUERO DAR ESSE PRESENTE"}</S.UpdateOwnerButton>
        )}
      </S.OwnerContainer>
    </S.Container>
  )
}
