import styled from "styled-components"

export const Container = styled.div`
  width: 100vw;
  height: 100vh;
  z-index: 1000;

  position: absolute;
  top: 0;
  left: 0;

  background-color: rgba(0, 0, 0, 0.4);

  display: flex;
  justify-content: center;
  align-items: center;
`

export const Body = styled.div`
  background-color: white;
  padding: 10px;
  border-radius: 8px;
  width: 100%;
  margin: 20px;
`

export const Title = styled.h1``

export const ButtonWrapper = styled.div`
  display: flex;

  button {
    width: 100%;

    &:first-child {
      margin-right: 10px;
    }
  }
`

export const Input = styled.input`
  height: 40px;
  font-size: 16px;
  width: 100%;

  margin: 20px 0;
  padding-left: 10px;
  padding-right: 10px;
`
