import styled from "styled-components"
import { ButtonProps } from "."

export const Button = styled.button<ButtonProps>`
  height: 40px;
  background-color: #f25565;
  color: white;
  font-size: 16px;
  border-radius: 15px;
  cursor: pointer;
  padding: 12px 20px;
  border: none;
`
