import { Categories } from "core/types"
import { FormEvent, useState } from "react"
import { addItem } from "services/item.service"
import * as S from "./styles"

const PASSWORD = "123"

export default function NewItem() {
  const [itemName, setItemName] = useState("")
  const [category, setCategory] = useState("")
  const [password, setPassword] = useState(Categories[0])

  async function handleAddNewItem(event: FormEvent) {
    event.preventDefault()

    if (!itemName) return alert("Nome do item não pode ser vazio")

    if (password !== PASSWORD) return alert("Senha inválida")

    try {
      await addItem({ name: itemName, owner: "", category })

      setItemName("")

      alert("Item adicionado com sucesso")
    } catch (error) {
      alert("Erro ao criar item")
      console.log("Erro ao criar item", error)
    }
  }

  return (
    <div>
      <S.Form onSubmit={handleAddNewItem}>
        <input
          placeholder="Item"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
        />

        <label htmlFor="categories">Selecione uma categoria:</label>
        <select
          name="categories"
          id="categories"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          {Categories.map((c) => (
            <option value={c}>{c}</option>
          ))}
        </select>

        <input
          placeholder="senha"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button type="submit">Adicionar</button>
      </S.Form>
    </div>
  )
}
