import { CheckItem, Header, Modal } from "components"
import { useEffect, useState } from "react"
import { BiBed } from "react-icons/bi"
import { getItems } from "services/item.service"
import * as S from "./styles"
import { Item } from "core/types"

export default function Items() {
  const [items, setItems] = useState<Item[]>([])
  const [modalOpen, setModalOpen] = useState(false)
  const [modalTitle, setModalTitle] = useState("")

  const kitchenItems = items.filter((item) => item.category === "cozinha")
  const bathroomItems = items.filter((item) => item.category === "banheiro")
  const bedroomItems = items.filter((item) => item.category === "quarto")
  const roomItems = items.filter((item) => item.category === "sala")
  const othersItems = items.filter((item) => item.category === "outros")

  function handleModalOpen() {
    setModalOpen(true)
  }

  function handleModalClose() {
    setModalOpen(false)
  }

  function handleAddOwner(itemName: string) {
    setModalTitle(itemName)
    handleModalOpen()
  }

  function handleAnonymous() {
    console.log("saving as anonymous")
    handleModalClose()
  }

  function handleMyName(myName: string) {
    console.log("my name is", myName)
    handleModalClose()
  }

  useEffect(() => {
    getItems((snapshot) => {
      const itemList: Item[] = []

      Object.keys(snapshot.val()).forEach((key) => {
        const currentItem = snapshot.val()[key] as Item

        itemList.push({
          id: key,
          name: currentItem.name,
          owner: currentItem.owner,
          category: currentItem.category,
        })
      })

      setItems(itemList)
    })
  }, [])

  return (
    <S.Container>
      <Header />

      <Modal
        open={modalOpen}
        onClose={handleModalClose}
        title={modalTitle}
        onAnonymous={handleAnonymous}
        onMyName={handleMyName}
      />

      <S.ListsWrapper>
        <S.ItemList>
          <S.IconWrapper>
            <BiBed size={90} color="#327FA6" />
            <h1>Cozinha</h1>
          </S.IconWrapper>

          <S.Separator />

          {kitchenItems.map((item) => (
            <CheckItem
              key={item.id}
              item={item}
              onAddOwner={() => handleAddOwner(item.name)}
            />
          ))}
        </S.ItemList>

        <S.ItemList>
          <S.IconWrapper>
            <BiBed size={90} color="#327FA6" />
            <h1>Quarto</h1>
          </S.IconWrapper>

          <S.Separator />

          {bedroomItems.map((item) => (
            <CheckItem
              key={item.id}
              item={item}
              onAddOwner={() => handleAddOwner(item.name)}
            />
          ))}
        </S.ItemList>

        <S.ItemList>
          <S.IconWrapper>
            <BiBed size={90} color="#327FA6" />
            <h1>Banheiro</h1>
          </S.IconWrapper>

          <S.Separator />

          {bathroomItems.map((item) => (
            <CheckItem
              key={item.id}
              item={item}
              onAddOwner={() => handleAddOwner(item.name)}
            />
          ))}
        </S.ItemList>

        <S.ItemList>
          <S.IconWrapper>
            <BiBed size={90} color="#327FA6" />
            <h1>Sala</h1>
          </S.IconWrapper>

          <S.Separator />

          {roomItems.map((item) => (
            <CheckItem
              key={item.id}
              item={item}
              onAddOwner={() => handleAddOwner(item.name)}
            />
          ))}
        </S.ItemList>

        <S.ItemList>
          <S.IconWrapper>
            <BiBed size={90} color="#327FA6" />
            <h1>Outros</h1>
          </S.IconWrapper>

          <S.Separator />

          {othersItems.map((item) => (
            <CheckItem
              key={item.id}
              item={item}
              onAddOwner={() => handleAddOwner(item.name)}
            />
          ))}
        </S.ItemList>
      </S.ListsWrapper>
    </S.Container>
  )
}
