import styled from "styled-components"
import { ButtonProps } from "."

export const Button = styled.button<ButtonProps>`
  height: 40px;
  color: white;
  font-size: 16px;
  border-radius: 24px;
  cursor: pointer;
  padding: 12px 20px;
  border: none;

  background-color: ${({ kind }) => {
    switch (kind) {
      case "info":
        return "#327FA6"
      case "danger":
        return "#f25565"
      default:
        return "#327FA6"
    }
  }};
`
